(33, 'JOINS:Tulis kueri SQL untuk menemukan film dengan tahun dan genre. Kembalikan judul film, tahun film, dan judul genre.', 'SELECT film_judul, film_tahun, genre_judul FROM film NATURAL JOIN genre_film NATURAL JOIN genre;', '[{\"film_judul\":\"Aliens\",\"film_tahun\":1986,\"genre_judul\":\"Action\"},{\"film_judul\":\"Lawrence of Arabia\",\"film_tahun\":1962,\"genre_judul\":\"Adventure\"},{\"film_judul\":\"Deliverance\",\"film_tahun\":1972,\"genre_judul\":\"Adventure\"},{\"film_judul\":\"Princess Mononoke\",\"film_tahun\":1997,\"genre_judul\":\"Animation\"},{\"film_judul\":\"Annie Hall\",\"film_tahun\":1977,\"genre_judul\":\"Comedy\"},{\"film_judul\":\"The Usual Suspects\",\"film_tahun\":1995,\"genre_judul\":\"Crime\"},{\"film_judul\":\"The Shawshank Redemption\",\"film_tahun\":1994,\"genre_judul\":\"Crime\"},{\"film_judul\":\"Trainspotting\",\"film_tahun\":1996,\"genre_judul\":\"Drama\"},{\"film_judul\":\"Slumdog Millionaire\",\"film_tahun\":2008,\"genre_judul\":\"Drama\"},{\"film_judul\":\"Seven Samurai\",\"film_tahun\":1954,\"genre_judul\":\"Drama\"},{\"film_judul\":\"Back to the Future\",\"film_tahun\":1985,\"genre_judul\":\"Drama\"},{\"film_judul\":\"The Innocents\",\"film_tahun\":1961,\"genre_judul\":\"Horror\"},{\"film_judul\":\"Beyond the Sea\",\"film_tahun\":2004,\"genre_judul\":\"Music\"},{\"film_judul\":\"Vertigo\",\"film_tahun\":1958,\"genre_judul\":\"Mystery\"},{\"film_judul\":\"Eyes Wide Shut\",\"film_tahun\":1999,\"genre_judul\":\"Mystery\"},{\"film_judul\":\"Spirited Away\",\"film_tahun\":2001,\"genre_judul\":\"Mystery\"},{\"film_judul\":\"American Beauty\",\"film_tahun\":1999,\"genre_judul\":\"Romance\"},{\"film_judul\":\"Blade Runner\",\"film_tahun\":1982,\"genre_judul\":\"Thriller\"},{\"film_judul\":\"The Deer Hunter\",\"film_tahun\":1978,\"genre_judul\":\"War\"}]', 1),
(34, 'JOINS:Tulis kueri SQL untuk menemukan semua film dengan tahun, genre, dan nama sutradara.', 'SELECT film_judul, film_tahun, genre_judul, sutradara_nama_depan, sutradara_nama_belakang FROM film NATURAL JOIN genre_film NATURAL JOIN genre NATURAL JOIN sutradara_film NATURAL JOIN sutradara;', '[{\"film_judul\":\"Aliens\",\"film_tahun\":1986,\"genre_judul\":\"Action\",\"sutradara_nama_depan\":\"James\",\"sutradara_nama_belakang\":\"Cameron\"},{\"film_judul\":\"Lawrence of Arabia\",\"film_tahun\":1962,\"genre_judul\":\"Adventure\",\"sutradara_nama_depan\":\"David\",\"sutradara_nama_belakang\":\"Lean\"},{\"film_judul\":\"Deliverance\",\"film_tahun\":1972,\"genre_judul\":\"Adventure\",\"sutradara_nama_depan\":\"John\",\"sutradara_nama_belakang\":\"Boorman\"},{\"film_judul\":\"Princess Mononoke\",\"film_tahun\":1997,\"genre_judul\":\"Animation\",\"sutradara_nama_depan\":\"Hayao\",\"sutradara_nama_belakang\":\"Miyazaki\"},{\"film_judul\":\"Annie Hall\",\"film_tahun\":1977,\"genre_judul\":\"Comedy\",\"sutradara_nama_depan\":\"Woody\",\"sutradara_nama_belakang\":\"Allen\"},{\"film_judul\":\"The Usual Suspects\",\"film_tahun\":1995,\"genre_judul\":\"Crime\",\"sutradara_nama_depan\":\"Bryan\",\"sutradara_nama_belakang\":\"Singer\"},{\"film_judul\":\"The Shawshank Redemption\",\"film_tahun\":1994,\"genre_judul\":\"Crime\",\"sutradara_nama_depan\":\"Frank\",\"sutradara_nama_belakang\":\"Darabont\"},{\"film_judul\":\"Trainspotting\",\"film_tahun\":1996,\"genre_judul\":\"Drama\",\"sutradara_nama_depan\":\"Danny\",\"sutradara_nama_belakang\":\"Boyle\"},{\"film_judul\":\"Slumdog Millionaire\",\"film_tahun\":2008,\"genre_judul\":\"Drama\",\"sutradara_nama_depan\":\"Danny\",\"sutradara_nama_belakang\":\"Boyle\"},{\"film_judul\":\"The Innocents\",\"film_tahun\":1961,\"genre_judul\":\"Horror\",\"sutradara_nama_depan\":\"Jack\",\"sutradara_nama_belakang\":\"Clayton\"},{\"film_judul\":\"Beyond the Sea\",\"film_tahun\":2004,\"genre_judul\":\"Music\",\"sutradara_nama_depan\":\"Kevin\",\"